    private void verActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_verActionPerformed
        if(agregado){
            new VentanaVisitante(JDBC.getJDBC().getLogin(miembro.getEmail()),usser).setVisible(true);
            dispose();
        }else{
            enviarSolicitud();
        }
    }//GEN-LAST:event_verActionPerformed

    private void eliminarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_eliminarActionPerformed
        ImageIcon ico = JDBC.getJDBC().getIconoUsuario(this.miembro.getEmail());
        int result = JOptionPane.showConfirmDialog(null,"Esta usted seguro que desea eliminar a :\n"+this.miembro.getNombre()
                                ,"Eliminar contacto",JOptionPane.YES_NO_CANCEL_OPTION, JOptionPane.WARNING_MESSAGE, ico);
        if(result == JOptionPane.YES_OPTION){
            JDBC.getJDBC().eliminarContactoMutuamente(this.usser.getPerfil().getCodigoPe(),this.miembro.getEmail(),this.usser.getEmail());
            usser.getPerfil().eliminarContacto(this.miembro.getEmail());
            JOptionPane.showMessageDialog(null,"Se ha eliminado a :\n"+miembro.getNombre()+"\nde sus amigos",
                                                        "contacto eliminado",JOptionPane.INFORMATION_MESSAGE);
            VentanaPerfil.refresh(usser);
            dispose();
        }
    }//GEN-LAST:event_eliminarActionPerformed

