    private void llenarInformacion() {
        this.setTitle(miembro.getNik());
        ((ImagePanel)avatar).setBackgroundImage(JDBC.getJDBC().getAvatar(miembro.getEmail()));
        email.setText(miembro.getEmail());
        nombre.setText(miembro.getNombre());
        
    }
    public void cambiarEstado(){
        agregado = false;
        ver.setText("Enviar solicitud de amistad");
    }

    public void mostrarEliminar(boolean b) {
        eliminar.setVisible(b);
    }

    private void enviarSolicitud() {
        if(!usser.getEmail().equals(miembro.getEmail())){
            ImageIcon icono = JDBC.getJDBC().getIconoUsuario(miembro.getEmail());
            JDBC.getJDBC().agregarSolicitud(this.usser,this.miembro.getEmail());
            JOptionPane.showMessageDialog(null,miembro.getNombre(), "Solicitud de amistad enviada",JOptionPane.INFORMATION_MESSAGE,icono);
            dispose();
        }
    }
