    private void ingresar() {
        boolean succes = true;
        if(!(email.getText().length()>0)){
            JOptionPane.showMessageDialog(null,"Ingrese un correo electronico","Error: campo vacio",JOptionPane.ERROR_MESSAGE);
            succes = false;
        }else if(!(password.getText().length()>0)){
            JOptionPane.showMessageDialog(null,"Ingrese su password","Error: campo vacio",JOptionPane.ERROR_MESSAGE);
            succes = false;
        }else if(succes){
            try {
                Usuario u = JDBC.getJDBC().getLogin(email.getText());
                
                if(u==null){
                    JOptionPane.showMessageDialog(null,"Ingrese una direccion de email registrada","E-mail invalido",JOptionPane.ERROR_MESSAGE);
                }else if(u.getPassword().equals(password.getText())) {
                    System.out.println("Coneccion correcta para el siguiente usuario\n"+u);
                    VentanaInicio.getVentanaInicio(u).setVisible(true);
                    new Reproductor("src/resource/welcome.WAV").run();
                    dispose();
                }else {
                    JOptionPane.showMessageDialog(null,"Password incorrecto","Error: conexion",JOptionPane.ERROR_MESSAGE);
                }
                
            } catch (Exception e) {
                
            }
        }
    }

